    var canvas = document.getElementById('canvas');
    var ctx = canvas.getContext('2d');

    // Cria o jogador
    var jogador = {
      x: 250,
      y: 250,
      width: 50,
      height: 50,
      color: 'red'
    };

    // Cria os obst√°culos
    var obstaculos = [];
    for (var i = 0; i < 10; i++) {
      var obstaculo = {
        x: Math.random() * 500,
        y: Math.random() * 500,
        width: 50,
        height: 50,
        color: 'blue'
      };

      obstaculos.push(obstaculo);
    }

    // Desenha o jogador
    function desenhaJogador() {
      ctx.fillStyle = jogador.color;
      ctx.fillRect(jogador.x, jogador.y, jogador.width, jogador.height);
    }

    // Desenha os obst√°culos
    function desenhaObstaculos() {
      for (var i = 0; i < obstaculos.length; i++) {
        var obstaculo = obstaculos[i];

        ctx.fillStyle = obstaculo.color;
        ctx.fillRect(obstaculo.x, obstaculo.y, obstaculo.width, obstaculo.height);
      }
    }

    // Verifica se o jogador colidiu com algum obst√°culo
    function verificaColisao() {
      for (var i = 0; i < obstaculos.length; i++) {
        var obstaculo = obstaculos[i];

        if (jogador.x < obstaculo.x + obstaculo.width &&
            jogador.x + jogador.width > obstaculo.x &&
            jogador.y < obstaculo.y + obstaculo.height &&
            jogador.y + jogador.height > obstaculo.y) {
          return true;
        }
      }

      return false;
    }

    // Atualiza o jogo
    function atualiza() {
      // Limpa a tela
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Desenha o jogador
      desenhaJogador();

      // Desenha os obst√°culos
      desenhaObstaculos();

      // Verifica se o jogador colidiu com algum obst√°culo
      if (verificaColisao()) {
        // Se colidiu, exibe uma mensagem de derrota
        ctx.fillStyle = 'white';
        ctx.font = '30px Arial';
        ctx.fillText('Voc√™ perdeu!', 250, 250);
      }
    }

    // Loop do jogo
    setInterval(atualiza, 1000 / 60);
