<head>
  <title>Jogo da Velha</title>
</head>
<body>
  <h1>Jogo da Velha</h1>
  <div id="tabuleiro">
    <div class="casa" id="0"></div>
    <div class="casa" id="1"></div>
    <div class="casa" id="2"></div>
    <div class="casa" id="3"></div>
    <div class="casa" id="4"></div>
    <div class="casa" id="5"></div>
    <div class="casa" id="6"></div>
    <div class="casa" id="7"></div>
    <div class="casa" id="8"></div>
  </div>
  <div id="mensagem"></div>
  <script>
    // Vari√°veis do jogo
    var jogador = "X";
    var tabuleiro = ["", "", "", "", "", "", "", "", ""];
    var fimDeJogo = false;

    // Fun√ß√£o para iniciar o jogo
    function iniciarJogo() {
      // Limpa o tabuleiro
      for (var i = 0; i < 9; i++) {
        tabuleiro[i] = "";
      }

      // Define o jogador inicial
      jogador = "X";

      // Habilita o evento de clique nas casas
      var casas = document.querySelectorAll(".casa");
      for (var i = 0; i < casas.length; i++) {
        casas[i].addEventListener("click", jogar);
      }

      // Limpa a mensagem de fim de jogo
      document.getElementById("mensagem").innerHTML = "";

      // Mostra o jogador atual
      exibirJogadorAtual();
    }

    // Fun√ß√£o para jogar
    function jogar(e) {
      // Verifica se o jogo j√° acabou
      if (fimDeJogo) {
        return;
      }

      // Verifica se a casa est√° vazia
      var casa = e.target;
      if (tabuleiro[casa.id] !== "") {
        return;
      }

      // Marca a casa com o s√≠mbolo do jogador
      tabuleiro[casa.id] = jogador;

      // Desenha o s√≠mbolo na casa
      casa.innerHTML = jogador;

      // Verifica se o jogador venceu
      if (verificarVencedor()) {
        fimDeJogo = true;
        exibirMensagemFimDeJogo(jogador + " venceu!");
        return;
      }

      // Verifica se houve empate
      if (tabuleiro.every(function(casa) { return casa !== ""; })) {
        fimDeJogo = true;
        exibirMensagemFimDeJogo("Empate!");
        return;
      }

      // Troca o jogador
      jogador = jogador === "X" ? "O" : "X";

      // Mostra o jogador atual
      exibirJogadorAtual();
    }

    // Fun√ß√£o para verificar se o jogador venceu
    function verificarVencedor() {
      // Linhas
      for (var i = 0; i < 3; i++) {
        if (tabuleiro[i * 3] !== "" && tabuleiro[i * 3] === tabuleiro[i * 3 + 1] && tabuleiro[i * 3 + 1] === tabuleiro[i * 3 + 2]) {
          return true;
        }
      }

      // Colunas
      for (var i = 0; i < 3; i++) {
        if (tabuleiro[i] !== "" && tabuleiro[i] === tabuleiro[i + 3] && tabuleiro[i + 3] === tabuleiro[i + 6]) {
          return true;
        }
      }

      // Diagonais
      if (tabuleiro[0] !== "" && tabuleiro[0] === tabuleiro[4] && tabuleiro[4] === tabuleiro[8]) {
        return true;
      }
      if (tabuleiro[2] !== "" && tabuleiro[2] === tabuleiro[4] && tabuleiro[4] === tabuleiro[6]) {
        return true;
      }

      return false;
    }

    // Fun√ß√£o para exibir a mensagem de fim de jogo
    function exibirMensagemFimDeJogo(mensagem) {
      document.getElementById("mensagem").innerHTML = mensagem;
    }

    // Fun√ß√£o para exibir o jogador atual
    function exibirJogadorAtual() {
      document.getElementById("mensagem").innerHTML = "Jogador atual: " + jogador;
    }

    // Inicia o jogo
    iniciarJogo();
  </script>
</body>
</html>
